import {Link} from "react-router-dom"

const TodoCard = ({todo, users}) => {

  return (
    (<div className="card">
      <p>Username:  <Link to={`/users/${todo.user}`}>{todo.user.username}</Link></p>
      {/* want this to link to "Profile" 
      (http://172.20.171.160:3000/todos/profile) */}
      <p>Type of task: {todo.name}</p>
      <p>Task: {todo.task}</p>
      <p>Completed: {todo.completed ? "true" : "false"} </p>
    </div>)

  )
}

export default TodoCard
