import {useState, useEffect} from "react"
import {Link, useParams} from "react-router-dom"
const TodoCard = ({todo}) => {
    const {id} = useParams()
    const [todoObj, setTodoObj] = useState(null);
    useEffect(() => {   
        if (!todos) {
            fetch(`http://localhost:9393/todos/${id}`)
            .then(resp => resp.json())
            .then(todos => setTodoObj(todo))
        }
    }, [todo, id]);

    const finalTodo = todo ? todo : todoObj
    if (!finalTodo) return <h1>Loading...</h1>
  return (
    <div>
        <h3>Name: <Link to={`/todos${finalTodo.id}`}>{finalTodo.name}</Link></h3>
        <h4>Task: {finalTodo.task}</h4>
        <h4>Completed: {finalTodo.completed} </h4>
    </div>
  )
}

export default TodoCard
