import {useState, useEffect} from "react"
import {Link, useParams} from "react-router-dom"

const TodoCard = ({todos}) => {
    const {id} = useParams()
    const [todoObj, setTodoObj] = useState(null);
    useEffect(() => {   
        if (!todos) {
            fetch(`http://127.0.0.1:9393/todos/new`)
            .then(resp => resp.json())
            .then(todos => setTodoObj(todos))
        }
    }, [todos, id]);

    const finalTodo = todos ? todos : todoObj
    if (!finalTodo) return <p>Loading...</p>
  return (
    <div>
        <h3>Username: <Link to={`/todos/new${finalTodo.user_id}`}>{finalTodo.name}</Link></h3>
        <p>Type of task: {finalTodo.task}</p>
        <p>Task: {finalTodo.task}</p>
        <h4>Completed: {finalTodo.completed} </h4>
    </div>
  )
}

export default TodoCard
