import {useState, useEffect} from "react"
import {Link, useParams} from "react-router-dom"

const ExisitingTodoContainer = ({user}) => {
  const {id} = useParams()
  const [userObj, setUserObj] = usesState(null);
  useEffect(() => {
    if (!user) {

      fetch(`http://127.0.0.1:9292/${id}`)
      .then((r) => r.json())
      .then((user) => console.log(user));
    }
  }, [user, id]);


  const finalPlant = user ? user : userObj
    if (!finalUser) return <h1>Loading...</h1>
  return (
    <div>
        <h3>Name: <Link to={`/user/${finalUser.id}`}>{finalUser.name}</Link></h3>
        <h4>Species: {finalUser.species}</h4>
        <h4>Sun Exposure: {finalPlant.sun_exposure} (hrs/day)</h4>
        <h4>Watering Frequency: {finalPlant.watering_freq} (days/week)</h4>
        <h4>Uploaded by: {finalPlant.user?.username || "No creator!"}</h4>
    </div>
  )
}

export default ExisitingTodoContainer
