import {useState, useEffect} from "react"
import {useParams} from "react-router-dom"

const TodoCard = ({todos}) => {
    const {user_id} = useParams()
    const [todoObj, setTodoObj] = useState(null);
    useEffect(() => {   
        if (!todos) {
            fetch(`http://127.0.0.1:9393/todos/new`)
            .then(resp => resp.json())
            .then(todos => setTodoObj(todos))
        }
    }, [todos, user_id]);

    const finalTodo = todos ? todos : todoObj
    if (!finalTodo) return <p>Loading...</p>
  return (
    <div>
        <p>Username: {finalTodo.user_id}</p>
          {/* <Link to={`/todos/new${finalTodo.user_id}`}>{finalTodo.name}</Link></h3> */}
        <p>Type of task: {finalTodo.name}</p>
        <p>Task: {finalTodo.task}</p>
        <p>Completed: {finalTodo.completed} </p>
    </div>
  )
}

export default TodoCard
