import TodoCard from "./TodoCard"
import {useState, useEffect} from "react"

const TodoList = () => {
    const [todoList, setTodoList] = useState(null);
    const [userList, setUserList] = useState(null);

    function Refresh(event){
        event.preventDefault();
        fetch(`http://127.0.0.1:9393/todos/new`)
             .then(resp => resp.json())
             .then(todos => setTodoList(todos))

        fetch(`http://127.0.0.1:9393/users`)
        .then(resp => resp.json())
        .then(users => setUserList(users))

        Refresh();
    }

    //const renderTodos = todoList.map(todo => <TodoCard key={todo.id} todos={todo}/>)
    return (
        <div onLoad={Refresh}>
            <button onClick={Refresh}>REFRESH</button>
            {todoList ? todoList.map(todo => <TodoCard key={todo.id} todo={todo} users={userList}/>) : <p>Please Refresh</p>}
        </div>
    )
}

export default TodoList
