import {useState} from "react"
import {useHistory} from "react-router-dom"


const TodoForm = () => {
    const [todos, setTodo] = useState({
        name: "",
        task: "",
        completed: false
    });

    const history = useHistory()

    const handleChange = (e) => {
        setTodo({
            ...todos,
            [e.target.name]: e.target.value
        })
    }
    const handleSubmit = e => {
        e.preventDefault()
        if ([todos.name, todos.task, todos.completed].some(val => val.trim() === "")) {
            alert("You must fill in all the information please!")
        }

        const newTodo = {
            name: todos.name,
            task: todos.task,
            completed: todos.completed            
        }

        fetch("http://http://127.0.0.1:9292/todos", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newTodo)
        })
        .then(() => history.push("/todos"))

    }
    return (
        <>
            <h3>Create a new Todo List</h3>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input onChange={handleChange} type="text" name="name" value={todos.name} required/><br />

                <label htmlFor="task">Task</label>
                <input onChange={handleChange} type="text" name="task" value={todos.task} required/><br />

                <label htmlFor="completed">Completed</label>
                <input onChange={handleChange} type="text" name="completed" value={todos.completed} required/><br />
                <input type="checkbox" value="Create Todo List" />
            </form>
        </>
    )
}

export default TodoForm
