import {useState} from "react"
import {useHistory} from "react-router-dom"


const TodoForm = () => {
    const [todo, setTodo] = useState({
        name: "",
        task: "",
        completed: false
    });

    const history = useHistory()

    const handleChange = (e) => {
        setTodo({
            ...todo,
            [e.target.name]: e.target.value
        })
    }
    const handleSubmit = e => {
        e.preventDefault()
        if ([todo.name, todo.task].some(val => val.trim() === "")) {
            alert("You must fill in all the information please!")
        }

        fetch(`http://127.0.0.1:9393/users`)
        .then(resp => resp.json())


        const newTodo = {
            username: todo.username,
            name: todo.name,
            task: todo.task,
            completed: todo.false            
        }

        fetch(`http://http://127.0.0.1:9393/todos/new`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newTodo)
        })
        .then(() => history.push("/todos/new"))

    }
    return (
        <>
            <form onSubmit={handleSubmit}>
            <h3>Create a new Todo List</h3>
            &nbsp;
            &nbsp;
                <label htmlFor="username">User Name</label>
            {/* <select name="selectList" id="selectList">
                {test.map((element) => {
                    return <option value={element}>{element.username}</option>
                })}
            </select> */}
            &nbsp;
            &nbsp;
                <input onChange={handleChange} type="text" name="user" value={todo.user} required/><br />

                <label htmlFor="name">Type("chore", "task", etc.)</label>
                <input onChange={handleChange} type="text" name="name" value={todo.name} required/><br />

                <label htmlFor="task">Task</label>
                <input onChange={handleChange} type="text" name="task" value={todo.task} required/><br />
                &nbsp;
                &nbsp;
                <label htmlFor="completed">Completed</label>
                &nbsp;
                &nbsp;
                <input onChange={handleChange} type="checkbox" name="completed?" value={todo.completed} required/><br />
                <input type="submit" value="Create Todo List" />
            </form>
        </>
    )
}

export default TodoForm
