import {useState, useEffect} from "react"
import {Link, useParams} from "react-router-dom"

const TodoCard = ({todos}) => {
    // const {users} = useParams()
    // const [todoObj, setTodoObj] = useState([]);

    const [todoObj, setTodoObj] = useState(null);

    // useEffect(() => {   
    //     if (!todos) {
    //         fetch(`http://127.0.0.1:9393/todos/new`)
    //         .then(resp => resp.json())
    //         .then(todos => setTodoObj(todos))
    //     }
    // }, [todos, users]);

    function GetTodoList(){
      fetch(`http://127.0.0.1:9393/todos/new`)
             .then(resp => resp.json())
             .then(todos => setTodoObj(todos))
    }

    const finalTodo = todos ? todos : todoObj
    if (!finalTodo) return <p>Loading...</p>
  return (
    <div>
        <p>Username: {todoObj.username} <Link to={`/todos/new`}></Link></p>
        <p>Type of task: {todoObj.name}</p>
        <p>Task: {todoObj.task}</p>
        <p>Completed: {todoObj.completed} </p>
    </div>
  )
}

export default TodoCard
