import {useState} from "react"
import {useHistory} from "react-router-dom"

const UserProfile = () => {
    const [id, setUser] = useState({
      user_id: null
    });

    const history = useHistory()

    const handleChange = (e) => {
      setUser({
        ...id,
        [e.target.name]: e.target.value
      })
    }

    const handleSubmit = (e) => {
      e.preventDefault(console.log("handling this submit"))
    }

console.log("trying to fetch")
  fetch(`http://127.0.0.1:9393/todos${id}`, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(id)
            })
            console.log("successfully fetching")
  return (

    <div>
      <h3>User Profile</h3>
        <p>Below you can either find todo lists for an existing username,</p>
        <p>or you can create a new username to begin making a new todo list</p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Existing Username</label>
        &nbsp;
        &nbsp;
        <input onChange={handleChange} type="text" name="user_id" value={id.username} required/><br />
        <input type="submit" value="Find Todo lists for this username" />
      </form>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Create a New Username</label>
        <input onChange={handleChange} type="text" name="user_id" value={id.username} required/><br />
        <input type="submit" value="Create New Username" />
    </form>
    </div>
  )
}

export default UserProfile
