import {useState, useEffect} from "react"
import TodoContainer from "../containers/TodoContainer";
import TodoList from "./TodoList";

const UserProfile = () => {
  const [newUser, setNewUser] = useState(null);
    const [user, setUser] = useState(null);

  useEffect = (() => {
   const render = render ? <TodoList/> : (null)
    console.log("working on it");
}, []);

  // }
      // above should be a ternary expression
    // to say if the user exists show their todo lists
    // otherwise prompt them to create a new username

    const handleExistingUserChange = (e) => {
      setUser(e.target.value)
    }

    const handleSubmitNewUser = (e) => {
      e.preventDefault(console.log("handling new submit"))

      fetch(`http://127.0.0.1:9393/users`)
        .then(resp => resp.json())
        .then(newUser => setNewUser(newUser))
    }
    // console.log(newUser)

    const handleSubmitExisting = (e) => {
      e.preventDefault(console.log("handling this submit"))

        fetch(`http://127.0.0.1:9393/users/by_name/${user}`)
          .then(resp => resp.json())
          .then(user => setUser(user))
    }
    console.log(user)

    return (

    <div>

      <h4>User Profile</h4>
        <p>Below you can either find todo lists for an existing username,</p>
        <p>or you can create a new username to begin making a new todo list</p>

      <form id="NewUserSubmit" onSubmit={handleSubmitNewUser}>
        <label htmlFor="username">Create New Username: &nbsp;</label>
        <input type="text" name="email" required/><br />
        <label htmlFor="email">Email: &nbsp;</label>
        <input type="text" name="user_id" required/><br />
        <input type="submit" value="Create New Username" />
      </form>

      &nbsp;
        &nbsp;
      &nbsp;
        &nbsp;

      <form id="ExistingUserSearch" onSubmit={handleSubmitExisting}>
        <label htmlFor="username">Username: &nbsp;</label>
        <input type="text" onChange={handleExistingUserChange} name="username" required/><br />
        <input type="submit" value="Find Todo lists for this user" />
      </form>

      &nbsp;
        &nbsp;
      &nbsp;
        &nbsp;
      &nbsp;
      {/* <TodoList/> */}
      {/* linking TodoList so that when we had the profile open it would populate the todo list for a specific username.
      Whereas existing todo lists is for all users
      the only problem with not having the refresh button is now the todo list for everyone is auto populating on the page */}
    </div>
  )
}

export default UserProfile
